import React, { useState, useEffect } from "react";
import { Button, Form, FormGroup, Input, Label, Row, Col } from "reactstrap";

const FormEmployee = ({ type, setFormVisible, formEdited }) => {
  const [name, setName] = useState("");
  const [age, setAge] = useState(0);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (type === 'create') {
      alert('created');
    } else {
      alert('edited');
    }
    setFormVisible(false);
  };

//   meanggil data dari parent ke children pada edit
  useEffect(() => {
    if (type == "edit") {
        setName(formEdited.name)
        setAge(formEdited.age)
    }
  }, [type, formEdited])

  return (
    <>
      <Row>
        <Form onSubmit={handleSubmit}>
          <>
            <FormGroup>
              <Label>Name</Label>
              <Input
                value={name}
                placeholder="Please Enter Your Name"
                onChange={(e) => setName(e.target.value)}
                required
              />
            </FormGroup>

            <FormGroup>
              <Label>Age</Label>
              <Input
                type="number"
                value={age}
                placeholder="Please Enter Your Name"
                onChange={(e) => setAge(e.target.value)}
                required
              />
            </FormGroup>

            <Row>
                <Col>
                <Button color="success" type="submit">Submit</Button>
                </Col>

                <Col>
                <Button color="success" type="submit" onClick={() => setFormVisible(false)} >Cancel</Button>
                </Col>
                
                {/* &nbsp;&nbsp; */}
                
            </Row>
          </>
        </Form>
      </Row>
    </>
  );
};

export default FormEmployee;
